import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./App.css";
import {
  Row,
  Col,
  Card,
  Modal,
  List,
  Image,
  Divider,
  Alert,
} from "antd";
import { getDogBreeds, getDogBreedsByName } from "./redux/actions/dogBreedActions";

function App() {
  const dispatch = useDispatch();
  const { breeds, breed_images } = useSelector((state) => state.breedReducer);
  const { errors } = useSelector((state) => state.errorReducer);

  const [isModalVisible, setIsModalVisible] = useState(false);
  const [dogBreed, setdogBreed] = useState([]);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };


  useEffect(() => {
    dispatch(getDogBreeds());
    //eslint-disable-next-line
  }, []);

  const onClose = (e) => {};

  return (
    <div className="App">
      <Col md={18} style={{ margin: "0 auto" }}>
        {errors !== null ? (
          <Alert
            message="We encountered an error"
            description={errors?.error_msg}
            type="error"
            closable
            onClose={onClose}
          />
        ) : null}

        <Divider orientation="left">
          <h3>Dogs by breeds</h3>
        </Divider>
        <Row gutter={[16, { xs: 8, sm: 16, md: 24, lg: 32 }]}>
          {Object.keys(breeds || {}).map((item, index) => (
            <Col md={8} key={index}>
              <Card
                className="dogItems"
                onClick={() => {
                  setdogBreed(item);
                  dispatch(getDogBreedsByName(item));
                  showModal();
                }}
              >
                {item}
              </Card>
            </Col>
          ))}
        </Row>
      </Col>

      <Modal
        title={`Showing Images for ${dogBreed}`}
        width={1000}
        bodyStyle={{ height: "80vh", overflow: "auto" }}
        footer={null}
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >

          <List
            grid={{
              gutter: 16,
              xs: 1,
              sm: 2,
              md: 4,
              lg: 4,
              xl: 5,
              xxl: 3,
            }}
            dataSource={breed_images}
            pagination={{
              onChange: page => {
                console.log(page);
              },
              pageSize: 20,
            }}
            renderItem={(item) => (
              <List.Item key={item.id}>
                <Card
                  className="dogImagesContainer"
                  bodyStyle={{ padding: "5px", textAlign: "center" }}
                >
                  <figure>
                    <Image
                      className={"dogImages"}
                      src={item}
                      placeholder={
                        <Image
                          preview={false}
                          src={
                            "data:image/png;base64,"
                          }
                          className={"dogImages"}
                        />
                      }
                    />
                  </figure>
                </Card>
              </List.Item>
            )}
          />
     
      </Modal>
    </div>
  );
}

export default App;
