import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import './App.css';
import axios from 'axios';
import {Row, Col, Card, Modal, List, Image, Skeleton, Divider } from 'antd';
import InfiniteScroll from 'react-infinite-scroll-component';
import { getDogBreeds } from './redux/actions/dogBreedActions';


function App() {
  const dispatch = useDispatch();
  const { breeds } =  useSelector((state) =>  state.breedReducer );
  // const { error_responses } =  useSelector((state) =>  state.errorReducer );

  const [dogBreedImages, setdogBreedImages] = useState([]);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [loading, setLoading] = useState(false);
  const [dogBreed, setdogBreed] = useState([]);


  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };


  function getDogByBreeds(breedName) {
    setdogBreed(breedName);
    setdogBreedImages([]);
    if (loading) {
      return;
    }
    setLoading(true);
    axios.get(`${process.env.REACT_APP_HOME_URL}/breed/${breedName}/images`)
    .then((res) => {
       setdogBreedImages(res.data.message);
       setLoading(false);
  })
    .catch((err) => {
      setLoading(false);
    })
  }

  useEffect(() => {
    dispatch(getDogBreeds());
    //eslint-disable-next-line
  }, [])

  console.log('errors', useSelector((state) =>  state.errorReducer ));

  return (
    <div className="App">
      <Col md={18} style={{ margin: "0 auto" }}>
          <Divider orientation="left"><h3>Dogs by breeds</h3></Divider>
          <Row gutter={[16, { xs: 8, sm: 16, md: 24, lg: 32 }]}>
          {Object.keys(breeds || {}).map((item, index) => (
              <Col md={8} key={index} >
                  <Card className="dogItems" onClick={() => {getDogByBreeds(item); showModal();}}>
                    {item}
                  </Card>
              </Col>
          ))}
          </Row>



</Col>


                <Modal title={`Showing Images for ${dogBreed}`} width={1000} footer={null} visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
                  <InfiniteScroll
                          dataLength={dogBreedImages.length}
                          next={() => getDogByBreeds(dogBreed)}
                          hasMore={dogBreedImages.length < 50}
                          loader={<Skeleton avatar paragraph={{ rows: 1 }} active />}
                          endMessage={<Divider plain>It is all, nothing more ü§ê</Divider>}
                          scrollableTarget="scrollableDiv"
                        >
                          <List
                            grid={{
                              gutter: 16,
                              xs: 1,
                              sm: 2,
                              md: 4,
                              lg: 4,
                              xl: 6,
                              xxl: 3,
                            }}
                            dataSource={dogBreedImages}
                            renderItem={item => (
                                      <List.Item key={item.id}>
                                        <Card className="dogImagesContainer" bodyStyle={{ padding: '5px', textAlign: 'center' }}>
                                              <figure>
                                                <Image
                                                    className={"dogImages"}
                                                    src={item} 
                                                    placeholder={
                                                      <Image
                                                        preview={false}
                                                        src={"data:image/png;base64,"} 
                                                        className={"dogImages"}
                                                      />
                                                    }
                                                />
                                              </figure>
                                        </Card>
                                      </List.Item>
                                    )}
                                  />
                    </InfiniteScroll>
                </Modal>


    </div>
  );
}

export default App;
